mapper.select:
SELECT book_order_id, order_number, create_date, update_date, acount_id FROM book_order_tb
AND acount_id=#{acountId}
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
AND ${orderName}
>=
<=
(SELECT ${orderName} FROM book_order_tb
AND acount_id=#{acountId}
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
ORDER BY ${orderName} ${orderWay} LIMIT #{pageNum}, 1 )
ORDER BY ${orderName} ${orderWay} LIMIT #{pageSize}
